mapper.select:


     and upload_user_id = #{uploadUserId}
     and event_type = #{eventType}
     and contact_person_name like concat('%', #{contactPersonName}, '%')
     and contact_phone = #{contactPhone}
     and address = #{address}
     and address_data = #{addressData}
     and event_description = #{eventDescription}

        AND date_format(occur_time,'%y%m%d') >= date_format(#{params.beginOccurTime},'%y%m%d')


        AND date_format(occur_time,'%y%m%d') <=  date_format(#{params.endOccurTime},'%y%m%d')

     and photo_url = #{photoUrl}
     and video_url = #{videoUrl}
     and status = #{status}
     and reply = #{reply}
     and dept_id = #{deptId}
     and id = #{id}

        AND upload_time >= #{params.beginUploadTime}


        AND upload_time <= #{params.endUploadTime}

    ${params.dataScope}


order by status,event_type,upload_time

mapper.insert:
insert into event_info
id, upload_user_id, event_type, contact_person_name, contact_phone, address, address_data, event_description, occur_time, photo_url, video_url, status, reply, upload_time, event_summarize, upload_user_type, dept_id
#{id}, #{uploadUserId}, #{eventType}, #{contactPersonName}, #{contactPhone}, #{address}, #{addressData}, #{eventDescription}, #{occurTime}, #{photoUrl}, #{videoUrl}, #{status}, #{reply}, #{uploadTime}, #{eventSummarize}, #{uploadUserType}, #{deptId}

mapper.update:
update event_info
upload_user_id = #{uploadUserId}, event_type = #{eventType}, contact_person_name = #{contactPersonName}, contact_phone = #{contactPhone}, address = #{address}, address_data = #{addressData}, event_description = #{eventDescription}, occur_time = #{occurTime}, photo_url = #{photoUrl}, video_url = #{videoUrl}, status = #{status}, reply = #{reply}, upload_time = #{uploadTime}, event_summarize = #{eventSummarize}, remark = #{remark}, dept_id = #{deptId}
where id = #{id}
